import React, { Component } from 'react'
import { Tabs, Tab, Grid, Cell, Card, CardTitle, CardActions, Button, CardMenu, IconButton, CardText } from 'react-mdl'

class receitas extends Component {
    constructor(props){
        super(props)
        this.state = {activeTab : 0}
    }
    
    toggleCategories() {

        if (this.state.activeTab === 0) {
            return (
               <div className="receitas">
                    <Card shadow={5} style={{width: '450px', margin: 'auto'}}>
                        <CardTitle style={{color: '#fff', height: '200px', background: 'url(data:image/jpeg;base64,) center / cover'}}>Cookies</CardTitle>
                        <CardText>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Mauris sagittis pellentesque lacus eleifend lacinia...
                        </CardText>
                        <CardActions border>
                            <Button colored>Ingredientes</Button>
                            <Button colored>Preparo</Button>
                            <Button colored>Receita Completa</Button>
                        </CardActions>
                        <CardMenu style={{color: '#fff'}}>
                            <IconButton name="share" />
                        </CardMenu>
                    
                    </Card>
                    <Card shadow={5} style={{width: '450px', margin: 'auto'}}>
                    <CardTitle style={{color: '#fff', height: '200px', background: 'url(data:image/jpeg;base64,) center/ cover'}}>Banoffee</CardTitle>
                    <CardText>
                        Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                        Mauris sagittis pellentesque lacus eleifend lacinia...
                    </CardText>
                    <CardActions border>
                        <Button colored>Ingredientes</Button>
                        <Button colored>Preparo</Button>
                        <Button colored>Receita Completa</Button>
                    </CardActions>
                    <CardMenu style={{color: '#fff'}}>
                        <IconButton name="share" />
                    </CardMenu>
                    </Card>
                </div>
            )
        }
        if (this.state.activeTab === 1) {
            return (
                <div></div>
            )
        }
    }

    render () {
        return(
            <div recipies-tabs>
                <Tabs activeTab={this.state.activeTab} onChange={(tabId) => this.setState({activeTab : tabId})} ripple>
                    <Tab>Doces</Tab>
                    <Tab>Salgados</Tab>
                </Tabs>

                <Grid>
                    <Cell col={12}>
                        <div className="content">{this.toggleCategories()}</div>
                        </Cell>
                </Grid>
            </div>
        )
    }
}

export default receitas